package loops;
/*write a program to print the addition of numbers 
 * from 1 to 20
 
 */
public class Addition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int sum=0;
for(int i=0; i<20; i++) {
	sum=sum+i;
}System.out.println("sum="+sum);
	}

}




