package loops;
//write a program to print 1 to 100 natural numbers with including comma pirnt in single line
public class NaturalNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=1;i<=100;i++) {
	if(i<100) {
	System.out.print(i+",");
}else {
	System.out.print(i);
}

	

}
	}
}