package loops;
// write a program to print sum of  odd numbers from 1 to 100
public class OddNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println("list of odd numbers from 1 to 100");
int sum=0;
for(int i=0;i<=100;i++) {

	if(i%2!=0) {
		sum=sum+i;
	}
	}System.out.println(sum);
	

	
	


}}