package loops;
// write a program to print the odd numbers from  1 to 100;
public class OddNo1 {

	public static void main(String[] args) {
		System.out.println(" List of odd numbers from 1 to 100 ");
		// TODO Auto-generated method stub
		for(int i=0;i<=100;i++) {
			if(i%2!=0)
				System.out.println(i);
		}

	}

}
