package B31;

public class Swapping1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// swap the value of a into b with no using extra variable
		int a=30;
		int b=60;
		a=a+b;
		b=a-b;
		a=a-b;
		System.out.println("a="+a);
		System.out.println("b="+b);
	}

}
