package B31;

public class Swapping2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// swap the value of a into b with using extra variable
		int a=45;
		int b=60;
		int c=0;
		c=a;
		a=b;
		b=c;
		System.out.println("a="+a);
		System.out.println("b="+b);
	}

}
